-- ----------------------------
-- Table structure for sa_system_menu
-- ----------------------------
DROP TABLE IF EXISTS `sa_system_menu`;
CREATE TABLE `sa_system_menu` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `parent_id` int unsigned DEFAULT NULL COMMENT '父ID',
  `level` varchar(500) DEFAULT NULL COMMENT '组级集合',
  `name` varchar(50) DEFAULT NULL COMMENT '菜单名称',
  `code` varchar(100) DEFAULT NULL COMMENT '菜单标识代码',
  `icon` varchar(50) DEFAULT NULL COMMENT '菜单图标',
  `route` varchar(200) DEFAULT NULL COMMENT '路由地址',
  `component` varchar(255) DEFAULT NULL COMMENT '组件路径',
  `redirect` varchar(255) DEFAULT NULL COMMENT '跳转地址',
  `is_hidden` smallint DEFAULT '1' COMMENT '是否隐藏 (1是 2否)',
  `is_layout` tinyint unsigned DEFAULT '1' COMMENT '继承layout',
  `type` char(1) DEFAULT '' COMMENT '菜单类型, (M菜单 B按钮 L链接 I iframe)',
  `generate_id` int DEFAULT '0' COMMENT '生成id',
  `generate_key` varchar(255) DEFAULT NULL COMMENT '生成key',
  `status` smallint DEFAULT '1' COMMENT '状态 (1正常 2停用)',
  `sort` smallint unsigned DEFAULT '0' COMMENT '排序',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  `created_by` int DEFAULT NULL COMMENT '创建者',
  `updated_by` int DEFAULT NULL COMMENT '更新者',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `delete_time` datetime DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=6030 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci ROW_FORMAT=DYNAMIC COMMENT='菜单信息表';

